import styled from 'styled-components';

export const Container = styled.div`
  margin: 40px auto;
  padding: 20px;
  max-width: 650px;
  text-align: center;
  box-shadow: var(--main-shadow);
  border-radius: 10px;
`;

export const Title = styled.h1`
  margin-bottom: 20px;
  padding: 5px;
  font-size: 44px;
  color: var(--primary);
  text-shadow: var(--main-shadow);
  box-shadow: var(--main-shadow);
  border-radius: 10px;
`;

export const Subtitle = styled.h2`
  margin-bottom: 20px;
  padding: 5px;
  font-size: 32px;

  border-radius: 10px;
`;
